update.foreach:
UPDATE T_MENU_ROLE
menu_code = #{item.menuCode}, role_power = #{item.rolePower}, role_name = #{roleName}, memo = #{item.memo}, is_used = #{item.isUsed}
WHERE id = #{item.id}

mapper.select:
SELECT m.*, mr.id mr_id, mr.menu_code mr_menu_code, mr.role_name mr_role_name, mr.role_power mr_role_power, mr.memo mr_memo, mr.is_used mr_is_used FROM `t_menu` m left join ( select * from t_menu_role WHERE 1 = 1
AND role_name = #{roleName}
) mr on mr.menu_code = m.menu_code join t_menu_role mr2 on mr2.menu_code = m.menu_code where 0 = 0
AND mr2.role_power = #{rolePower}
AND mr2.is_used = #{isUsed}
GROUP by id

SELECT d.*, i.id mr_id, i.menu_code mr_menu_code, i.role_name mr_role_name, i.role_power mr_role_power, i.memo mr_memo, i.is_used mr_is_used FROM t_menu d LEFT JOIN t_menu_role c ON c.menu_code = d.menu_code LEFT JOIN sys_roles b ON b.ROLE_NAME = c.role_power LEFT JOIN sys_users_roles a ON a.ROLE_ID = b.ROLE_ID LEFT JOIN ( SELECT f.* FROM t_menu e LEFT JOIN t_menu_role f ON e.menu_code = f.menu_code WHERE f.role_power = #{roleName} ) i ON i.menu_code = d.menu_code WHERE 1=1
AND i.is_used = #{isUsed}
AND d.is_used = '1' GROUP BY d.id;

SELECT d.*, i.id mr_id, i.menu_code mr_menu_code, i.role_name mr_role_name, i.role_power mr_role_power, i.memo mr_memo, i.is_used mr_is_used FROM t_menu d LEFT JOIN t_menu_role c ON c.menu_code = d.menu_code LEFT JOIN sys_roles b ON b.ROLE_NAME = c.role_power LEFT JOIN sys_users_roles a ON a.ROLE_ID = b.ROLE_ID LEFT JOIN ( SELECT f.* FROM t_menu e LEFT JOIN t_menu_role f ON e.menu_code = f.menu_code WHERE f.role_power = #{roleName} ) i ON i.menu_code = d.menu_code WHERE a.USER_ID =#{userId}
AND i.is_used = #{isUsed}
AND d.is_used = '1' AND c.is_used = '1' AND b.ENABLED = '1' AND a.ENABLED = '1' GROUP BY d.id;
